body.script:
//
(function() {
    'use strict';

    // Mobile menu toggle
    window.toggleMobileMenu = function() {
        document.querySelector('.nav-links').classList.toggle('open');
    };

    // Sidebar functions
    window.openSidebar = function() {
        document.getElementById('sidebar').classList.add('open');
        document.getElementById('backdrop').classList.add('show');
        document.body.style.overflow = 'hidden';
    };

    window.closeSidebar = function() {
        document.getElementById('sidebar').classList.remove('open');
        document.getElementById('backdrop').classList.remove('show');
        document.body.style.overflow = '';
    };

    // Panel functions
    window.openPanelByZone = function(zoneName) {
        var panel = document.getElementById('panel-' + zoneName);
        if (!panel) return;

        closeAllPanels();
        panel.classList.add('open');
        document.getElementById('backdrop').classList.add('show');
        document.body.style.overflow = 'hidden';
        panel.scrollTop = 0;
    };

    window.closeAllPanels = function() {
        var panels = document.querySelectorAll('.panel');
        panels.forEach(function(panel) {
            panel.classList.remove('open');
        });
        document.getElementById('sidebar').classList.remove('open');
        document.getElementById('backdrop').classList.remove('show');
        document.body.style.overflow = '';

        var zones = document.querySelectorAll('.zone');
        zones.forEach(function(zone) {
            zone.classList.remove('active');
        });
    };

    // Zone click handlers
    document.addEventListener('DOMContentLoaded', function() {
        var zones = document.querySelectorAll('.zone');
        zones.forEach(function(zone) {
            zone.addEventListener('click', function(e) {
                e.stopPropagation();
                var zoneName = zone.dataset.zone;
                if (zoneName) openPanelByZone(zoneName);
            });
        });

        // Circumpunct link
        var circumpunctLink = document.querySelector('.circumpunct-link');
        if (circumpunctLink) {
            circumpunctLink.addEventListener('click', function(e) {
                e.preventDefault();
                openPanelByZone('circumpunct');
            });
        }

        // Panel navigation links
        var panelLinks = document.querySelectorAll('.panel-links a[data-zone]');
        panelLinks.forEach(function(link) {
            link.addEventListener('click', function(e) {
                e.preventDefault();
                var zoneName = link.dataset.zone;
                if (zoneName) openPanelByZone(zoneName);
            });
        });

        // Escape key
        document.addEventListener('keydown', function(e) {
            if (e.key === 'Escape') closeAllPanels();
        });
    });

    // Particle Background
    var canvas = document.getElementById('particles');
    if (!canvas) return;

    var ctx = canvas.getContext('2d');
    var particles = [];
    var centerX, centerY, boundaryRadius;

    function resize() {
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
        centerX = canvas.width / 2;
        centerY = canvas.height / 2;
        boundaryRadius = Math.min(canvas.width, canvas.height) * 0.15;
        initParticles();
    }

    function Particle() {
        this.reset();
    }

    Particle.prototype.reset = function() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.speedX = (Math.random() - 0.5) * 0.5;
        this.speedY = (Math.random() - 0.5) * 0.5;
        this.size = Math.random() * 2 + 1;
        this.opacity = Math.random() * 0.5 + 0.2;
        var types = ['body', 'mind', 'soul'];
        this.type = types[Math.floor(Math.random() * types.length)];
    };

    Particle.prototype.update = function() {
        this.x += this.speedX;
        this.y += this.speedY;

        if (this.x < 0) this.x = canvas.width;
        if (this.x > canvas.width) this.x = 0;
        if (this.y < 0) this.y = canvas.height;
        if (this.y > canvas.height) this.y = 0;
    };

    Particle.prototype.draw = function() {
        var r, g, b;
        if (this.type === 'body') {
            r = 163; g = 113; b = 247;
        } else if (this.type === 'mind') {
            r = 240; g = 180; b = 41;
        } else {
            r = 88; g = 166; b = 255;
        }

        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.fillStyle = 'rgba(' + r + ',' + g + ',' + b + ',' + this.opacity + ')';
        ctx.fill();
    };

    function initParticles() {
        var count = Math.min(60, Math.floor((canvas.width * canvas.height) / 20000));
        particles = [];
        for (var i = 0; i < count; i++) {
            particles.push(new Particle());
        }
    }

    function animate() {
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        particles.forEach(function(p) {
            p.update();
            p.draw();
        });

        // Draw connections
        for (var i = 0; i < particles.length; i++) {
            for (var j = i + 1; j < particles.length; j++) {
                var dx = particles[i].x - particles[j].x;
                var dy = particles[i].y - particles[j].y;
                var dist = Math.sqrt(dx * dx + dy * dy);
                if (dist < 120) {
                    ctx.beginPath();
                    ctx.moveTo(particles[i].x, particles[i].y);
                    ctx.lineTo(particles[j].x, particles[j].y);
                    ctx.strokeStyle = 'rgba(255, 255, 255, ' + ((1 - dist / 120) * 0.15) + ')';
                    ctx.lineWidth = 0.5;
                    ctx.stroke();
                }
            }
        }

        requestAnimationFrame(animate);
    }

    window.addEventListener('resize', resize);
    resize();
    animate();
})();
//
